import React, { useEffect, useRef, useState } from "react";
import "./App.css";
import Temp from "./assets/clouds.png";
import wind from "./assets/wind.png";
import humidity from "./assets/humidity.png";

const App = () => {
  const [cityName, setCityName] = useState();
  const [temp, setTemp] = useState("");
  const [windSpeed, setWindSpeed] = useState("");
  const [humidity, setHumidity] = useState("");
  const [cloudStatus, setCloudStatus] = useState("");
  const inputRef = useRef();

  useEffect(() => {
    fetch(
      `https://api.openweathermap.org/data/2.5/weather?q=germany&appid=c06770a44708c57d9f3039741c98abff&units=metric`
    )
      .then((response) => response.json())
      .then((response) => {
        setTemp(response.main.temp);
        setWindSpeed(response.wind.speed);
        setHumidity(response.main.humidity);
        setCloudStatus(response.weather);
        console.log(temp, windSpeed, humidity, cloudStatus);
      });
  }, []);

  const handleClick = () => {
    if (inputRef.current.value == "") {
      alert("Enter the city name and click on submit !!!");
    }
    setCityName(inputRef.current.value);
    console.log(cityName);
    console.log("button clicked");
  };

  return (
    <div className="container">
      <div className="container-card">
        <h1>Weather</h1>
        <div className="inputs">
          <input placeholder="Enter the city Name:" ref={inputRef} />
          <button onClick={handleClick}>Submit</button>
        </div>
        <div className="temp">
          <img src={Temp} alt="" />
          <h2>25Â°c</h2>
        </div>
        <div className="weather-details">
          <div className="wind">
            <img src={wind} alt="" />
            <h2>
              25 <br /> <span>wind speed</span>
            </h2>
          </div>
          <div className="humidity">
            <img src={humidity} alt="" />
            <h2>
              25 <br /> <span>Humidity</span>
            </h2>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
